/* src/components/MosaicComponent.css */

.mosaic-loading,
.mosaic-error {
  text-align: center;
  font-size: 1.2rem;
  padding: 2rem;
  opacity: 0.8;
}

/* Mosaico responsivo */
.mosaic-grid {
  width: 100%;
  display: grid;
  gap: 6px;
  grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
}

.mosaic-tile {
  width: 100%;
  background: var(--bg-soft);
  overflow: hidden;
  border-radius: 6px;
  height: 0;
  padding-bottom: 100%; /* cuadrado */
  position: relative;
}

.mosaic-tile img {
  position: absolute;
  inset: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform 0.25s ease;
}

.mosaic-tile:hover img {
  transform: scale(1.05);
}
