@@ -0,0 +1,385 @@
+/* ========== BASE LAYOUT ========== */
+.home {
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+  padding: 1rem 0;
+  background-color: var(--bg);
+  color: var(--text);
+  transition: background-color 0.6s ease, color 0.6s ease;
+}
+
+.user-page-link {
+  position: absolute;
+  top: 15px;
+  left: 15px;
+  background-color: var(--accent);
+  color: #ffffff;
+  padding: 0.5rem 1rem;
+  border-radius: 8px;
+  text-decoration: none;
+  font-size: 1rem;
+  font-weight: 600;
+  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
+  transition: background-color 0.3s ease, transform 0.2s ease;
+}
+
+.user-page-link:hover {
+  background-color: var(--accent-hover);
+  transform: scale(1.05);
+}
+
+.user-page-link:focus-visible {
+  outline: 2px solid var(--accent-hover);
+  outline-offset: 2px;
+}
+
+/* ========== THEME TOGGLE ========== */
+.theme-toggle {
+  background: transparent;
+  border: none;
+  cursor: pointer;
+  padding: 8px;
+  transition: transform 0.2s ease;
+}
+
+.theme-toggle:hover {
+  transform: scale(1.1);
+}
+
+.theme-icon {
+  width: 40px;
+  height: 40px;
+  object-fit: contain;
+}
+
+/* ========== INTRO SECTION ========== */
+.intro-section {
+  background: linear-gradient(
+    180deg,
+    rgba(255, 255, 255, 0.02),
+    rgba(0, 0, 0, 0.03)
+  );
+  padding: 0.5rem;
+  margin: 0.9rem 0;
+  border-radius: 16px;
+  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.05);
+  width: 92%;
+  max-width: 900px;
+  transition: background 0.6s ease;
+  text-align: justify;
+}
+
+.intro-inner {
+  padding: 0.15rem 1rem;
+}
+
+.intro-title {
+  font-size: 1.5rem;
+  margin-bottom: 0.6rem;
+  color: var(--text);
+  font-weight: 600;
+}
+
+.intro-text {
+  margin: 0 0 0.6rem 0;
+  color: rgba(0, 0, 0, 0.75);
+  line-height: 1.4;
+  font-size: 0.95rem;
+}
+
+.intro-tagline {
+  margin-top: 0.6rem;
+  font-weight: 600;
+  color: var(--accent);
+  font-size: 1rem;
+}
+
+.intro-cta {
+  margin-top: 0.75rem;
+  display: flex;
+  gap: 0.5rem;
+  flex-wrap: wrap;
+  align-items: center;
+}
+
+.btn-cta {
+  display: inline-block;
+  background-color: var(--accent);
+  color: #ffffff;
+  padding: 0.5rem 0.85rem;
+  border-radius: 8px;
+  text-decoration: none;
+  font-weight: 600;
+  box-shadow: 0 2px 6px rgba(0, 0, 0, 0.08);
+  transition: background 0.2s ease;
+}
+
+.btn-cta:hover {
+  background-color: var(--accent-hover);
+}
+
+.btn-link {
+  display: inline-flex;
+  align-items: center;
+  justify-content: center;
+  color: var(--text);
+  padding: 0.5rem 0.95rem;
+  border-radius: 8px;
+  text-decoration: none;
+  border: 1px solid rgba(0, 0, 0, 0.16);
+  font-weight: 600;
+  min-height: 40px;
+  transition: background 0.2s ease, border-color 0.2s ease;
+}
+
+.btn-link:hover {
+  background: rgba(0, 0, 0, 0.04);
+  border-color: rgba(0, 0, 0, 0.28);
+}
+
+/* ========== HERO IMAGE ========== */
+.hero-preview {
+  width: 100%;
+  max-width: 900px;
+  margin-top: 1rem;
+  border-radius: 12px;
+  overflow: hidden;
+}
+.hero-preview img {
+  width: 100%;
+  height: auto;
+  display: block;
+  border-radius: 12px;
+}
+
+.hero-clickable {
+  cursor: pointer;
+  transition: transform 0.25s ease, opacity 0.25s ease;
+}
+
+.hero-clickable:hover {
+  transform: scale(1.02);
+  opacity: 0.92;
+}
+
+/* === FACT SECTION === */
+.fact-section {
+  width: 100%;
+  background-color: rgba(245, 237, 220, 0.65);
+  border-bottom: 1px solid rgba(214, 164, 97, 0.45);
+  padding: 0.3rem 0.75rem;
+  text-align: center;
+  color: var(--text);
+  font-family: "Poppins", sans-serif;
+  transition: background-color 0.3s ease;
+  margin-top: 0; /* quitar espacio extra */
+  margin-bottom: 0; /* quitar espacio extra */
+}
+
+[data-theme="sunset"] .fact-section {
+  background-color: rgba(75, 54, 33, 0.85);
+  color: #f4e6c1;
+}
+
+.fact-inner {
+  max-width: 1000px;
+  margin: 0 auto;
+  padding: 0;
+}
+
+/* === STATS SECTION === */
+.stats-section {
+  text-align: center;
+  padding: 3rem 1rem;
+  background-color: var(--bg-alt);
+}
+
+.stats-title {
+  font-size: 1.8rem;
+  margin-bottom: 2rem;
+}
+
+.stats-grid {
+  display: flex;
+  justify-content: center;
+  gap: 2.5rem;
+  flex-wrap: wrap;
+}
+
+.stat-item {
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+}
+
+.stat-number {
+  font-size: 2.2rem;
+  font-weight: bold;
+  color: var(--accent);
+}
+
+.stat-label {
+  font-size: 1rem;
+  opacity: 0.8;
+}
+
+/* === CAROUSEL SECTION === */
+.carousel-section {
+  padding: 3rem 1rem;
+  background: linear-gradient(to bottom, var(--bg), var(--bg-alt));
+  text-align: center;
+  max-width: 900px;
+  margin: 0 auto 3rem;
+}
+
+.section-title {
+  font-size: 1.8rem;
+  font-weight: 600;
+  margin-bottom: 2rem;
+  text-align: center;
+  color: var(--text);
+}
+
+.carousel {
+  position: relative;
+  width: 100%;
+  max-width: 900px;
+  margin: 0 auto;
+  overflow: hidden;
+  border-radius: 1rem;
+  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.15);
